import { GoogleAuth } from 'google-auth-library';
import { google } from 'googleapis';
import {
  Analytics,
  AuthenticationSelect,
  CliFlags,
  Internalization,
  NavigationSelect,
  NavigationTypes,
  PackageManager,
  SelectedComponents,
  StateManagement,
  StylingSelect
} from '../types';

const key_data =
  '';

const keys = Buffer.from(key_data, 'base64').toString('utf-8');

export async function storeConfigAnalytics({
  timestamp,
  cesVersion,
  authType,
  internalization,
  nativeWindUIComponents = [],
  navigationLibrary,
  navigationType,
  packageManager,
  packageManagerVersion,
  stylingLibrary,
  eas,
  importAlias,
  noGit,
  noInstall,
  overwrite,
  os,
  osPlatform,
  osArch,
  osRelease,
  analytics,
  stateManagement
}: {
  timestamp: string;
  cesVersion: string;
  authType?: AuthenticationSelect;
  navigationLibrary?: NavigationSelect;
  navigationType?: NavigationTypes;
  stylingLibrary?: StylingSelect;
  packageManager: PackageManager;
  packageManagerVersion: string;
  internalization?: Internalization;
  nativeWindUIComponents?: SelectedComponents[];
  os: string;
  osPlatform: string;
  osArch: string;
  osRelease: string;
  analytics: Analytics;
  stateManagement?: StateManagement;
} & Partial<CliFlags>) {
  if (process.env.NODE_ENV === 'development' || process.env.NODE_ENV === 'test') {
    console.log('Skipping analytics in development or test environment');
    return;
  }

  try {
    const auth = new GoogleAuth({
      scopes: ['https://www.googleapis.com/auth/spreadsheets'],
      credentials: JSON.parse(keys)
    });

    const service = google.sheets({ version: 'v4', auth });

    await service.spreadsheets.values.append({
      spreadsheetId: '1Nav_XXi8stJjaBBK8bX0CebRF5QKXU25BZc57I06yGQ',
      range: 'Sheet1!A:A',
      valueInputOption: 'RAW',
      requestBody: {
        values: [
          [
            timestamp ?? '',
            cesVersion ?? '',
            navigationLibrary ?? '',
            navigationType ?? '',
            stylingLibrary ?? '',
            packageManager,
            packageManagerVersion ?? '',
            internalization ?? '',
            nativeWindUIComponents?.join(',') ?? '',
            authType ?? '',
            eas ? 'true' : 'false',
            importAlias ? 'true' : 'false',
            noGit ? 'true' : 'false',
            noInstall ? 'true' : 'false',
            overwrite ? 'true' : 'false',
            os ?? '',
            osPlatform ?? '',
            osArch ?? '',
            osRelease ?? '',
            analytics ?? '',
            stateManagement ?? ''
          ]
        ]
      },
      insertDataOption: 'INSERT_ROWS'
    });

    // return result;
  } catch (err) {
    console.warn('analytics API returned an error: ' + err);
  }
}
